using Microsoft.EntityFrameworkCore;
using System.Collections.Generic;
using System.Linq;

namespace MvcMusicStore.Models
{
    public class MusicStoreDBContext : DbContext
    {
        public DbSet<Album> Albums { get; set; }
        public DbSet<Genre> Genres { get; set; }
        public DbSet<Artist> Artists { get; set; }
        public DbSet<Cart> Carts { get; set; }
        public DbSet<Order> Orders { get; set; }
        public DbSet<OrderDetail> OrderDetails { get; set; }

        protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)
        => optionsBuilder.UseSqlite("Data Source=MusicStore.db");

        protected override void OnModelCreating(ModelBuilder modelBuilder)
        {
            base.OnModelCreating(modelBuilder);
            List<Genre> genres = GetGenreData();
            AddSeedData(modelBuilder, genres);
            List<Artist> artists = GetArtistData();
            AddSeedData(modelBuilder, artists);
            AddSeedData(modelBuilder, GetAlbumData(genres, artists));
        }

        private static void AddSeedData<T>(ModelBuilder modelBuilder, IEnumerable <T> list)
            where T : class
        {
            foreach (T item in list)
            {
                modelBuilder.Entity<T>().HasData(item);
            }
        }

